import React, { useState } from 'react';

function App() {
  const [xmlFile, setXmlFile] = useState(null);
  const [validationMessage, setValidationMessage] = useState('');
  const [yamlOutput, setYamlOutput] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isValid, setIsValid] = useState(false);
  const [format, setFormat] = useState('docker'); // Ajouter un état pour le format

  const handleFileChange = (event) => {
    setXmlFile(event.target.files[0]);
  };

  const handleFormatChange = (event) => {
    setFormat(event.target.value); // Mettre à jour le format (docker ou kubernetes)
  };

  const handleValidate = async (event) => {
    event.preventDefault();
    if (!xmlFile) {
      setErrorMessage('Please choose a file to upload');
      setValidationMessage('');
      return;
    }

    const formData = new FormData();
    formData.append('xml_file', xmlFile);
    formData.append('format', format);  // Ajouter le format dans la requête

    try {
      const response = await fetch('http://localhost:5000/validate_and_generate', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();
      if (result.status === 'success') {
        setIsValid(true);
        setValidationMessage('Validation successful!');
        setErrorMessage('');
      } else {
        setIsValid(false);
        setValidationMessage('');
        setErrorMessage(result.message);
      }
    } catch (error) {
      setErrorMessage('An error occurred: ' + error.message);
      setValidationMessage('');
    }
  };

  const handleGenerateYaml = async (event) => {
    event.preventDefault();
    if (!isValid) {
      setErrorMessage('XML file is not valid. Cannot generate YAML.');
      return;
    }

    const formData = new FormData();
    formData.append('xml_file', xmlFile);
    formData.append('format', format);

    try {
      const response = await fetch('http://localhost:5000/validate_and_generate', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();
      if (result.status === 'success') {
        setYamlOutput(result.yaml);
        setErrorMessage('');
      } else {
        setYamlOutput('');
        setErrorMessage(result.message);
      }
    } catch (error) {
      setYamlOutput('');
      setErrorMessage('An error occurred: ' + error.message);
    }
  };

  return (
    <div className="App">
      <h1>DevOps Configuration Manager</h1>
      <form>
        <label>
          Choose XML file:
          <input type="file" onChange={handleFileChange} />
        </label>
        <div>
          <label>
            Format:
            <select value={format} onChange={handleFormatChange}>
              <option value="docker">Docker Compose</option>
              <option value="kubernetes">Kubernetes</option>
            </select>
          </label>
        </div>
        <div>
          <button type="button" onClick={handleValidate}>Validate</button>
          <button type="button" onClick={handleGenerateYaml} disabled={!isValid}>Generate YAML</button>
        </div>
      </form>

      {validationMessage && (
        <div>
          <h3>Validation Result:</h3>
          <p>{validationMessage}</p>
        </div>
      )}

      {yamlOutput && (
        <div>
          <h3>Generated YAML:</h3>
          <pre>{yamlOutput}</pre>
        </div>
      )}

      {errorMessage && (
        <div className="error-message">
          <p>{errorMessage}</p>
        </div>
      )}
    </div>
  );
}

export default App;
