import React, { useState } from 'react';

function App() {
  const [xmlFile, setXmlFile] = useState(null);
  const [validationMessage, setValidationMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isValid, setIsValid] = useState(false);  // Ajout de l'état de validation
  const [dockerFile, setDockerFile] = useState('');
  const [kubernetesFile, setKubernetesFile] = useState('');

  const handleFileChange = (event) => {
    setXmlFile(event.target.files[0]);
  };

  const handleValidate = async (event) => {
    event.preventDefault();
    if (!xmlFile) {
      setErrorMessage('Please choose a file to upload');
      setValidationMessage('');
      return;
    }

    const formData = new FormData();
    formData.append('xml_file', xmlFile);

    try {
      const response = await fetch('http://localhost:5000/validate_and_generate', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();
      if (result.status === 'success') {
        setIsValid(true);  // Le fichier est valide
        setValidationMessage('Validation successful!');
        setDockerFile(result.docker_file);
        setKubernetesFile(result.kubernetes_file);
        setErrorMessage('');
      } else {
        setIsValid(false);  // Le fichier est invalide
        setValidationMessage('');
        setErrorMessage(result.message);
      }
    } catch (error) {
      setErrorMessage('An error occurred: ' + error.message);
      setValidationMessage('');
    }
  };

  const handleDownload = (filename) => {
    window.location.href = `http://localhost:5000/download/${filename}`;
  };

  return (
    <div className="App">
      <h1>DevOps Configuration Manager</h1>
      <form>
        <label>
          Choose XML file:
          <input type="file" onChange={handleFileChange} />
        </label>
        <div>
          <button type="button" onClick={handleValidate}>Validate</button>
        </div>
      </form>

      {validationMessage && (
        <div>
          <h3>Validation Result:</h3>
          <p>{validationMessage}</p>
        </div>
      )}

      {errorMessage && (
        <div className="error-message">
          <p>{errorMessage}</p>
        </div>
      )}

      {isValid && (
        <div>
          <h3>Download Files:</h3>
          <button onClick={() => handleDownload(dockerFile)}>Download Docker Compose</button>
          <button onClick={() => handleDownload(kubernetesFile)}>Download Kubernetes Deployment</button>
        </div>
      )}
    </div>
  );
}

export default App;
