import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [file, setFile] = useState(null);
  const [yaml, setYaml] = useState('');
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        }
      });

      // Affichage du chemin du fichier YAML généré
      setYaml(response.data.yaml_file);
    } catch (error) {
      setError('Error uploading file');
      console.error("There was an error uploading the file!", error);
    }
  };

  const handleDownload = (filename) => {
    window.location.href = `http://localhost:5000/download_yaml/${filename}`;
  };

  return (
    <div>
      <h1>DevOps Configuration Manager</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload XML</button>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      {yaml && (
        <div>
          <h2>Generated YAML:</h2>
          <pre>{yaml}</pre>
          <button onClick={() => handleDownload('docker-compose.yml')}>Download YAML</button>
        </div>
      )}
    </div>
  );
};

export default App;
