import React, { useState } from 'react';

function App() {
  const [xmlFile, setXmlFile] = useState(null);
  const [yamlOutput, setYamlOutput] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleFileChange = (event) => {
    setXmlFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!xmlFile) {
      setErrorMessage('Please choose a file to upload');
      return;
    }

    const formData = new FormData();
    formData.append('xml_file', xmlFile);

    try {
      const response = await fetch('http://localhost:5000/validate_and_generate', {
        method: 'POST',
        body: formData,
      });

      const result = await response.json();
      if (response.ok) {
        setYamlOutput(result.yaml);
        setErrorMessage('');
      } else {
        setYamlOutput('');
        setErrorMessage(result.message);
      }
    } catch (error) {
      setYamlOutput('');
      setErrorMessage('An error occurred: ' + error.message);
    }
  };

  return (
    <div className="App">
      <h1>DevOps Configuration Manager</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Choose XML file:
          <input type="file" onChange={handleFileChange} />
        </label>
        <button type="submit">Validate and Generate YAML</button>
      </form>

      {yamlOutput && (
        <div>
          <h3>Generated YAML:</h3>
          <pre>{yamlOutput}</pre>
        </div>
      )}

      {errorMessage && (
        <div className="error-message">
          <p>{errorMessage}</p>
        </div>
      )}
    </div>
  );
}

export default App;
